                } catch (InterruptedException e) {
                    e.printStackTrace();
                }
                mockService.query();
            }).start();
            cd2.countDown();
        }
        synchronized (this) {
            try {
                this.wait();
            } catch (InterruptedException e) {
                e.printStackTrace();
            }
        }
    }
}

