2.1.3.2. 同步代码块
/**
 * 服务模拟
 * @author zhuqiang
 * @version 1.0.1 2017/2/9 13:45
 * @date 2017/2/9 13:45
 * @since 1.0
 */
public class MockService {
    private Logger log = LoggerFactory.getLogger(getClass());
    final String code = "book";
    JedisPool pool = new JedisPool(new JedisPoolConfig(), "localhost");

    /**
     * 模拟从db查询数据
     * @return
     */
    private Map getDBBookMap() {
        //假设业务处理需要时间
        try {
            TimeUnit.MILLISECONDS.sleep(999);
        } catch (InterruptedException e) {
            e.printStackTrace();
        }
        Map mockData = new HashMap<>();
        for (int i = 0; i  query() {
        Jedis jedis = pool.getResource();
        try {
            String jsonStr = jedis.get(code);
            if (jsonStr != null) {
                return JSON.parseObject(jsonStr, Map.class);
            }
            synchronized (code) {
                jsonStr = jedis.get(code);
                if (jsonStr != null) {
                    return JSON.parseObject(jsonStr, Map.class);
                }
                count ++;
                log.info("================== no Cache");
                // 缓存未命中，则从数据库获取数据
                Map dbBookMap = getDBBookMap();

                jedis.setex(code, 1, JSON.toJSONString(dbBookMap));
                return dbBookMap;
            }
        } finally {
            jedis.close();  // 这里一定要关闭，否则会死锁
        }
    }

    public Integer getCount() {
        return count;
    }
}

